import javax.swing.JOptionPane;
// class name must be same as class file
public class GuessNumber {
// method for storing guessing variables
    public void guess(){
        // declaring variables
        String message = "Guess a number between 1 and 100";
        String title = "GUESS NUMBER GAME";
        int guess = Integer.parseInt(JOptionPane.showInputDialog(message, title));

        // loops while user does not guess the right number
        while (guess != 50) {
            if(guess > 50){
                message = "Nope! Too high!";
            } else if (guess < 50){
                message = "Nope! Too low!";
            }
            // read user's next guess
            guess = Integer.parseInt(JOptionPane.showInputDialog(message, title));
        }
        // message to display to user when guess correct number
        message = " GOOD JOB";
        JOptionPane.showMessageDialog(null, message);
    };
}
